mapper.select:
WITH noticeList AS ( (SELECT n.notice_no , n.writer_emp_no , e.emp_name , n.dept_code , di.division_code , IFNULL(di.division_name, 'ALL') AS division_name , n.notice_title , n.notice_view , n.create_datetime , n.is_pinned FROM notice n LEFT OUTER JOIN department d ON n.dept_code = d.dept_code LEFT OUTER JOIN division di ON d.division_code = di.division_code LEFT OUTER JOIN employee e ON n.writer_emp_no = e.emp_no
(di.division_code LIKE #{division} OR di.division_code IS NULL)
AND n.is_pinned LIKE 'Y'
)
UNION ALL
(SELECT n.notice_no , n.writer_emp_no , e.emp_name , n.dept_code , di.division_code , IFNULL(di.division_name, 'ALL') AS division_name , n.notice_title , n.notice_view , n.create_datetime , n.is_pinned FROM notice n LEFT OUTER JOIN department d ON n.dept_code = d.dept_code LEFT OUTER JOIN division di ON d.division_code = di.division_code LEFT OUTER JOIN employee e ON n.writer_emp_no = e.emp_no
n.notice_title LIKE CONCAT('%' , #{searchTitle}, '%')
AND di.division_code LIKE #{division}
AND n.is_pinned LIKE 'N'
) ) SELECT notice_no AS noticeNo , writer_emp_no AS empNo , emp_name AS empName , dept_code AS deptCode , division_code AS divisionCode , division_name AS divisionName , notice_title AS noticeTitle , notice_view AS noticeView , create_datetime AS createDate , is_pinned AS isPinned FROM noticeList ORDER BY CASE WHEN division_code IS NULL THEN 0 ELSE 1 END , is_pinned DESC , create_datetime DESC
